mapper.insert:
INSERT INTO t_address (
id, userId, nickname, phone, province, city, county, address, createDate, updateDate, state, label,applicationId
) VALUES (
#{id},#{userId},#{nickname},#{phone},#{province},#{city},#{county},#{address},#{createDate},#{updateDate},#{state},#{label},#{applicationId}
)


    select $uuid()

mapper.update:
UPDATE t_address
nickname = #{nickname}, phone = #{phone}, province = #{province}, city = #{city}, county = #{county}, address = #{address}, updateDate = #{updateDate},
WHERE id = #{id}
