mapper.select:
SELECT COUNT(id) FROM t_contract c
WHERE c.state != 1
AND (c.createUser = #{userId} OR c.companySaleUserId IN

    #{item}

)

    AND c.contractNo LIKE CONCAT('%',#{contractNo},'%')

SELECT c.*,uc.name as userName,ucb.name as companySaleUserName FROM t_contract c
LEFT JOIN t_user uc ON uc.id = c.createUser
LEFT JOIN t_user ucb ON ucb.id = c.companySaleUserId
WHERE c.state != 1
AND (c.createUser = #{userId} OR c.companySaleUserId IN

      #{item}

)

    AND c.contractNo LIKE CONCAT('%',#{contractNo},'%')

LIMIT #{pageStart} , #{pageSize};

SELECT c.*,coa.companyName companyPayName,cob.companyName companySaleName,cu.name companyPayCustomerName,u.name companySaleUserName FROM t_contract c LEFT JOIN t_company coa ON c.companyPayId = coa.id LEFT JOIN t_company cob ON c.companySaleId = cob.id LEFT JOIN t_customer cu ON c.companyPayCustomerId = cu.id LEFT JOIN t_user u ON c.companySaleUserId = u.id WHERE c.applicationId = #{applicationId} AND c.state != 1
AND (c.createUser = #{userId} OR c.companySaleUserId IN
#{item}
) AND (SELECT COUNT(tip.id) FROM t_invoice_plan tip WHERE tip.planState = '1' AND tip.contractId = c.id) > 0
AND c.contractNo LIKE CONCAT('%',#{contractNo},'%')
