mapper.insert:
INSERT INTO t_contract (
  createDate, updateDate, state, contractNo, versionNo, companyIdA, companyContractIdA, companyIdB, companyContractIdB, companyIdC, companyContractIdC, signDate, startDate, endDate, itemName, item, amt, applicationId, contactType, contactState, remark, paymentNum
) VALUES (
  #{createDate},#{updateDate},#{state},#{contractNo},#{versionNo},#{companyIdA},#{companyContractIdA},#{companyIdB},#{companyContractIdB},#{companyIdC},#{companyContractIdC},#{signDate},#{startDate},#{endDate},#{itemName},#{item},#{amt},#{applicationId},#{contactType},#{contactState},#{remark},#{paymentNum}
)

mapper.select:
SELECT * FROM t_contract c WHERE c.applicationId = #{applicationId} AND c.state != 1
AND c.contractNo LIKE CONCAT('%',#{contractNo},'%')
LIMIT #{pageStart} , #{pageSize};
