mapper.insert:
INSERT INTO t_customer (
name, companyId, birthday, sex, contactAddress, contactTel, jobLevel, email, createDate, updateDate, state
) VALUES (
#{name},#{companyId},#{birthday},#{sex},#{contactAddress},#{contactTel},#{jobLevel},#{email},#{createDate},#{updateDate},#{state}
)

mapper.update:
UPDATE t_customer
name = #{name}, birthday = #{birthday}, sex = #{sex}, contactAddress = #{contactAddress}, contactTel = #{contactTel}, jobLevel = #{jobLevel}, email = #{email}, updateDate = #{updateDate},
WHERE id = #{id}

mapper.select:
SELECT * FROM t_customer c WHERE c.applicationId = #{applicationId} AND c.state != 1
AND c.name LIKE CONCAT('%',#{name},'%')
LIMIT #{pageStart} , #{pageSize};

SELECT c.id, c.name FROM t_customer c WHERE c.applicationId = #{applicationId} AND c.state != 1

    AND c.name LIKE CONCAT('%',#{name},'%')


    AND c.companyId = #{companyId}
