    id,contractId, paymentPlanFact, versionNo, amt, planDate, planReceiveDate, receiveAmt, state, createDate, updateDate, planState
)
VALUES (
    #{id},#{contractId},#{paymentPlanFact},#{versionNo},#{amt},#{planDate},#{planReceiveDate},#{receiveAmt},#{state},#{createDate},#{updateDate},#{planState}
)


    select $uuid()

