mapper.insert:
INSERT INTO t_user (
createDate, updateDate, state, name, phoneNumber, mail, password, employeeNo, currAddress, homeAdress, birthday, idCardNo, applicationId, companyId
) VALUES (
#{createDate}, #{updateDate}, #{state}, #{name}, #{phoneNumber}, #{mail}, #{password}, #{employeeNo}, #{currAddress}, #{homeAdress}, #{birthday}, #{idCardNo}, #{applicationId}, #{companyId}
);

mapper.update:
UPDATE t_user
name = #{name}, mail = #{mail}, phoneNumber = #{phoneNumber}, employeeNo = #{employeeNo}, updateDate = #{updateDate},
WHERE 1 = 1 AND id = #{id}
